export const stringCode = `import "./global.css";
import { craftingStyles } from "./main";
//@ts-ignore
import { CraftClassesType } from "./src/types/typeManifest";
// Importing the CSS file generated by Galadriel.js
import './index.css';


// TOTAL DE 52 REGRAS

//@ts-ignore
const containerStyles = craftingStyles(() => {
    return {
        display: "$panel-flex",
        flexDirection: "$flex-orientation-row",
        justifyContent: "$organize-content-start",
        alignContent: "$match-content-center",
        flexWrap: "$flex-enclose-wrap",
        alignItems: "$adjust-stretch",
        overflow: "$excess-hidden",
        height: "100vh",
        background: "#a6c5e3",
        overflowY: "$excess-y-scroll",
        hover: {
            background: "#bb00aa",
            backgroundColor: "$some-class-name",
        },
        active: {
            backgroundColor: "#FCFF33",
            alignItems: "$adjust-center",
            overflow: "$excess-hidden",
        },
        minLargeDesktops: {
            background: "#99FF33",
            justifyContent: "$organize-content-center",
        },
        maxStandardSmartphones: {
            background: "#998475",
            flexDirection: "$flex-orientation-column",
        },
    };
});

const container = document.createElement("div");
container.onclick = () => console.log("click");
container.id = "container";
container.classList.add(...containerStyles.split(" "));

let count = 0;
const appendBtn = () => {
    const container = document.getElementById("container");
    const btn = document.createElement("button");

    btn.disabled = true;
    btn.classList.add(...craftingStyles(function () {
        let stls = {};
        if (count % 2 === 0) {
            stls = { simplifiedButton: "$element-button-light", backgroundColor: "$another-class-name" };
        } else {
            stls = { simplifiedButton: "$element-button-midnight-blue" };
        }

        return {
            ...stls,
            cursor: "$controller-crosshair", // 22 regras
        };
    }).split(" "));
    btn.innerText = "Click Me!";

    container?.append(btn);
    count++;
};

for (let i = 0; i <= 15; i++) {
    const button = document.createElement("button");

    button.addEventListener("click", () => appendBtn());

    const buttonStyles = craftingStyles(() => {
        let styles: CraftClassesType = {
            simplifiedButton: "$element-button-scarlet",
            display: "$panel-flex",
        };

        if (i === 0) {
            styles = { simplifiedButton: "$element-button-light-apricot" };
        } else if (i === 1) {
            styles = { simplifiedButton: "$element-button-sunset-orange" };
        } else if (i === 2) {
            styles = { simplifiedButton: "$element-button-scarlet" };
        } else if (i === 3) {
            styles = {
                simplifiedButton: "$element-button-candy-apple",
                color: "#FFF",
            };
        } else if (i === 4) {
            styles = { simplifiedButton: "$element-button-golden-poppy" };
        } else if (i === 5) {
            styles = { simplifiedButton: "$element-button-pale-red" };
        } else if (i === 6) {
            styles = { simplifiedButton: "$element-button-sunglow" };
        } else if (i === 7) {
            styles = { simplifiedButton: "$element-button-coral-red" };
        } else if (i === 8) {
            styles = { simplifiedButton: "$element-button-thistle" };
        } else if (i === 9) {
            styles = {
                simplifiedButton: "$element-button-byzantine-purple",
                color: "#FFF",
            };
        } else if (i === 10) {
            styles = { simplifiedButton: "$element-button-turquoise-green" };
        } else if (i === 11) {
            styles = { simplifiedButton: "$element-button-pale-pink" };
        } else if (i === 12) {
            styles = { simplifiedButton: "$element-button-dodger-blue" };
        } else if (i === 13) {
            styles = {
                simplifiedButton: "$element-button-midnight-blue",
                content: "' '",
                color: "#FFF",
                after: {
                    content: "' '",
                    width: "20vw",
                    height: "20vh",
                    backgroundColor: "#5416aa",
                    position: "absolute",
                    color: "#FFF",
                    top: "30px"
                },
                backgroundColor: "$another-class-name"
            };
        } else if (i === 14) {
            styles = { simplifiedButton: "$element-button-light" };
        }

        return {
            ...styles,
            cursor: "pointer",// 51 - regras
        };
    });

    button.classList.add(...buttonStyles.split(" "));
    button.innerText = "Click Here!";
    button.type = "button";

    container.appendChild(button);
}

const root = document.getElementById("root");

if (root) root.style.fontFamily = "sans-serif";

const nav = document.createElement("nav");

nav.classList.add(...craftingStyles(() => ({
    simplifiedNavbar: "$element-navbar-candy-apple",
})).split(" "))

nav.innerHTML = "
<div>
    <a href="/">Logo</a>
    <ul><li>Dropdown<ul><li>Item 5</li><li>Item 6</li><li>Item 7</li></ul><li>Item 1</li><li>Item 2</li><li>Item 3</li><li>Dropdown<ul><li>Item 5</li><li>Item 6</li><li>Item 7</li></ul></li></ul>
  </div>
";

const footer = document.createElement('footer');

footer.classList.add(...craftingStyles(() => ({
    simplifiedFooter: "$element-footer-candy-apple",
})).split(" "))

footer.innerHTML = "<div>
    <a href="/">Name</a>
    <div><p>Contact us: example@email.com | 123-456-7890</p></div><div><a href="#" target="_blank">Facebook</a><a href="#" target="_blank">Twitter</a><a href="#" target="_blank">Instagram</a></div><div><p>&copy; 2023 Your Website. All Rights Reserved.</p></div>
</div>";

root?.appendChild(nav);
root?.appendChild(container);
root?.appendChild(footer);` 